function searchBooks() {
    const searchInput = document.getElementById('searchInput').value.toLowerCase();
    const searchHP = document.getElementById('searchHP').value;
    const searchType = document.getElementById('searchType').value;
    const searchEvo = document.getElementById('searchEvo').value;
    const searchWeakness = document.getElementById('searchWeakness').value;
    const searchRetreat = document.getElementById('searchRetreat').value;
    const searchRarity = document.getElementById('searchRarity').value;

    const results = books.filter(book =>
        (book.name.toLowerCase().includes(searchInput) || book.abi.toLowerCase().includes(searchInput) || book.abieff.toLowerCase().includes(searchInput) || book.attack1.toLowerCase().includes(searchInput) || book.cost1.toLowerCase().includes(searchInput) || book.damage1.toLowerCase().includes(searchInput) || book.effect1.toLowerCase().includes(searchInput) || book.attack2.toLowerCase().includes(searchInput) || book.cost2.toLowerCase().includes(searchInput) || book.damage2.toLowerCase().includes(searchInput) || book.effect2.toLowerCase().includes(searchInput)) &&
        (book.HP == searchHP || !searchHP) &&
        (book.type == searchType || !searchType) &&
        (book.evo == searchEvo || !searchEvo) &&
        (book.weakness == searchWeakness || !searchWeakness) &&
        (book.retreat == searchRetreat || !searchRetreat) &&
        (book.rarity == searchRarity || !searchRarity)
    );

    const resultsContainer = document.getElementById('results');
    resultsContainer.innerHTML = '';

    results.forEach(book => {
        const bookElement = document.createElement('img');
        const path = `img/${book.name}.png`
        bookElement.src = path;
        resultsContainer.appendChild(bookElement);
    });

    if (results.length === 0) {
        resultsContainer.innerHTML = '<p>検索結果が見つかりませんでした。</p>';
    }
}
